import styled from 'styled-components'

interface SlideProps{
    imgURL : String,

}

export const Container=styled.div<SlideProps>`
    backgroundImage : ${ (props) =>(  `url(${props.imgURL})`   )};
    backgroundSize : contain;
    backgroundRepeat: no-repeat;
    width: 500px;
    height: 200px;
`;
